import React, { useState, useEffect } from 'react';
import axios from 'axios';

const TeacherManagement = ({ demoMode, backendOnline }) => {
  const [teachers, setTeachers] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTeachers();
  }, []);

  const fetchTeachers = async () => {
    try {
      setLoading(true);

      if (backendOnline) {
        const response = await axios.get('/api/teachers');
        setTeachers(response.data.data || []);
      } else {
        // Fallback demo data
        setTeachers([
          {
            _id: '1',
            name: 'Dr. Rajesh Kumar',
            email: 'rajesh.kumar@college.edu',
            department: 'Computer Science',
            maxHoursPerWeek: 40,
            qualifications: ['PhD in Computer Science'],
            subjects: ['CS101', 'CS102'],
            isActive: true
          },
          {
            _id: '2',
            name: 'Prof. Priya Sharma',
            email: 'priya.sharma@college.edu',
            department: 'Mathematics',
            maxHoursPerWeek: 35,
            qualifications: ['PhD in Mathematics'],
            subjects: ['MATH101', 'MATH102'],
            isActive: true
          }
        ]);
      }
    } catch (error) {
      console.error('Error fetching teachers:', error);
      // Fallback data
      setTeachers([
        {
          _id: '1',
          name: 'Dr. Rajesh Kumar',
          email: 'rajesh.kumar@college.edu',
          department: 'Computer Science',
          maxHoursPerWeek: 40,
          isActive: true
        }
      ]);
    } finally {
      setLoading(false);
    }
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">Teacher Management</h1>
        <button className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg flex items-center space-x-2">
          <i className="fas fa-plus"></i>
          <span>Add Teacher</span>
        </button>
      </div>

      <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
        <div className="px-6 py-4 border-b border-gray-200">
          <h2 className="text-lg font-semibold text-gray-900">
            Teachers List ({teachers.length})
          </h2>
        </div>
        <div className="overflow-x-auto">
          <table className="w-full">
            <thead className="bg-gray-50">
              <tr>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Teacher
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Department
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Subjects
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Max Hours
                </th>
                <th className="px-6 py-3 text-left text-xs font-medium text-gray-500 uppercase tracking-wider">
                  Status
                </th>
              </tr>
            </thead>
            <tbody className="bg-white divide-y divide-gray-200">
              {teachers.map((teacher) => (
                <tr key={teacher._id} className="hover:bg-gray-50">
                  <td className="px-6 py-4 whitespace-nowrap">
                    <div>
                      <div className="text-sm font-medium text-gray-900">
                        {teacher.name}
                      </div>
                      <div className="text-sm text-gray-500">
                        {teacher.email}
                      </div>
                    </div>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-blue-100 text-blue-800">
                      {teacher.department}
                    </span>
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {teacher.subjects?.join(', ') || 'No subjects assigned'}
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap text-sm text-gray-500">
                    {teacher.maxHoursPerWeek} hours/week
                  </td>
                  <td className="px-6 py-4 whitespace-nowrap">
                    <span className="px-2 inline-flex text-xs leading-5 font-semibold rounded-full bg-green-100 text-green-800">
                      Active
                    </span>
                  </td>
                </tr>
              ))}
            </tbody>
          </table>
        </div>
      </div>

      {teachers.length === 0 && (
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 text-center">
          <i className="fas fa-exclamation-triangle text-yellow-600 text-2xl mb-3"></i>
          <h3 className="text-lg font-semibold text-yellow-800">No Teachers Found</h3>
          <p className="text-yellow-700">There are no teachers in the system yet.</p>
        </div>
      )}
    </div>
  );
};

export default TeacherManagement;
