import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Dashboard = () => {
  const [stats, setStats] = useState({
    teachers: 0,
    classrooms: 0,
    subjects: 0,
    timetables: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, []);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);
      // In a real app, you'd have a dedicated dashboard endpoint
      // For now, we'll fetch counts from individual endpoints
      const [teachersRes, classroomsRes, subjectsRes, timetablesRes] = await Promise.all([
        axios.get('/api/teachers'),
        axios.get('/api/classrooms'),
        axios.get('/api/subjects'),
        axios.get('/api/timetable')
      ]);

      setStats({
        teachers: teachersRes.data.count || 0,
        classrooms: classroomsRes.data.count || 0,
        subjects: subjectsRes.data.count || 0,
        timetables: timetablesRes.data.count || 0
      });
    } catch (error) {
      toast.error('Failed to load dashboard data');
      console.error('Dashboard error:', error);
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ title, value, icon, color }) => (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
      <div className="flex items-center">
        <div className={`p-3 rounded-lg ${color}`}>
          <i className={`${icon} text-white text-xl`}></i>
        </div>
        <div className="ml-4">
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-2xl font-bold text-gray-900">
            {loading ? '...' : value}
          </p>
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
        <button
          onClick={fetchDashboardData}
          className="bg-primary-500 hover:bg-primary-600 text-white px-4 py-2 rounded-lg flex items-center space-x-2"
        >
          <i className="fas fa-refresh"></i>
          <span>Refresh</span>
        </button>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Teachers"
          value={stats.teachers}
          icon="fas fa-chalkboard-teacher"
          color="bg-blue-500"
        />
        <StatCard
          title="Classrooms"
          value={stats.classrooms}
          icon="fas fa-door-open"
          color="bg-green-500"
        />
        <StatCard
          title="Subjects"
          value={stats.subjects}
          icon="fas fa-book"
          color="bg-purple-500"
        />
        <StatCard
          title="Timetables"
          value={stats.timetables}
          icon="fas fa-calendar-alt"
          color="bg-orange-500"
        />
      </div>

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            Quick Actions
          </h3>
          <div className="space-y-3">
            <button className="w-full bg-primary-50 hover:bg-primary-100 text-primary-700 px-4 py-3 rounded-lg flex items-center space-x-3 text-left">
              <i className="fas fa-calendar-plus text-primary-500"></i>
              <span>Generate New Timetable</span>
            </button>
            <button className="w-full bg-green-50 hover:bg-green-100 text-green-700 px-4 py-3 rounded-lg flex items-center space-x-3 text-left">
              <i className="fas fa-robot text-green-500"></i>
              <span>AI Assistant</span>
            </button>
            <button className="w-full bg-purple-50 hover:bg-purple-100 text-purple-700 px-4 py-3 rounded-lg flex items-center space-x-3 text-left">
              <i className="fas fa-download text-purple-500"></i>
              <span>Export Data</span>
            </button>
          </div>
        </div>

        {/* NEP Compliance Status */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            NEP 2020 Compliance
          </h3>
          <div className="space-y-3">
            {[
              'Skill Development Periods',
              'Interdisciplinary Learning',
              'Activity/Project Blocks',
              'Flexible Learning Hours',
              'Balanced Workload'
            ].map((feature, index) => (
              <div key={index} className="flex items-center space-x-3">
                <i className="fas fa-check-circle text-green-500"></i>
                <span className="text-sm text-gray-700">{feature}</span>
              </div>
            ))}
          </div>
        </div>

        {/* System Status */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            System Status
          </h3>
          <div className="space-y-4">
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">Backend API</span>
              <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                Online
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">Database</span>
              <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                Connected
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">AI Service</span>
              <span className="px-2 py-1 bg-blue-100 text-blue-800 text-xs rounded-full">
                Ready
              </span>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
