import React from 'react';
import { Link, useLocation } from 'react-router-dom';

const Sidebar = ({ dbStatus, demoMode }) => {
  const location = useLocation();

  const menuItems = [
    { path: '/', icon: 'fas fa-home', label: 'Dashboard' },
    { path: '/teachers', icon: 'fas fa-chalkboard-teacher', label: 'Teachers' },
    { path: '/classrooms', icon: 'fas fa-door-open', label: 'Classrooms' },
    { path: '/subjects', icon: 'fas fa-book', label: 'Subjects' },
    { path: '/generate', icon: 'fas fa-calendar-plus', label: 'Generate' },
    { path: '/timetable', icon: 'fas fa-calendar-alt', label: 'Timetable' },
    { path: '/ai-assistant', icon: 'fas fa-robot', label: 'AI Assistant' },
  ];

  return (
    <div className="sidebar w-64 bg-gray-800 text-white">
      <div className="p-6">
        <div className="flex items-center space-x-3">
          <div className="w-10 h-10 bg-primary-500 rounded-lg flex items-center justify-center">
            <i className="fas fa-calendar-check text-white"></i>
          </div>
          <div>
            <h2 className="text-lg font-bold">NEP Scheduler</h2>
            <p className="text-xs text-gray-400">v1.0.0</p>
          </div>
        </div>
      </div>

      <nav className="mt-6">
        {menuItems.map((item) => (
          <Link
            key={item.path}
            to={item.path}
            className={`flex items-center px-6 py-3 text-sm font-medium transition-colors ${
              location.pathname === item.path
                ? 'bg-primary-600 text-white'
                : 'text-gray-300 hover:bg-gray-700 hover:text-white'
            }`}
          >
            <i className={`${item.icon} w-6 mr-3 text-center`}></i>
            {item.label}
          </Link>
        ))}
      </nav>

      <div className="absolute bottom-0 w-64 p-6 border-t border-gray-700">
        <div className={`flex items-center space-x-3 ${
          demoMode ? 'text-yellow-400' : 'text-green-400'
        }`}>
          <div className={`w-3 h-3 rounded-full ${
            demoMode ? 'bg-yellow-400' : 'bg-green-400'
          }`}></div>
          <div>
            <p className="text-sm font-medium">
              {demoMode ? 'Demo Mode' : 'System Online'}
            </p>
            <p className="text-xs text-gray-400">
              {demoMode ? 'Using sample data' : 'All systems operational'}
            </p>
          </div>
        </div>
        {demoMode && (
          <div className="mt-3 p-2 bg-yellow-900 bg-opacity-50 rounded text-xs text-yellow-200">
            <i className="fas fa-info-circle mr-1"></i>
            Connect database for full features
          </div>
        )}
      </div>
    </div>
  );
};

export default Sidebar;
