import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import Dashboard from './components/Dashboard';
import TeacherManagement from './components/TeacherManagement';
import ClassroomManagement from './components/ClassroomManagement';
import SubjectManagement from './components/SubjectManagement';
import TimetableGenerator from './components/TimetableGenerator';
import TimetableView from './components/TimetableView';
import AIAssistant from './components/AIAssistant';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import DatabaseStatus from './components/DatabaseStatus';

// Demo data for when backend is not available
const localDemoData = {
  teachers: [
    {
      _id: 'local-teacher-1',
      name: 'Dr. Rajesh Kumar',
      email: 'rajesh.kumar@college.edu',
      department: 'Computer Science',
      maxHoursPerWeek: 40,
      isActive: true
    },
    {
      _id: 'local-teacher-2',
      name: 'Prof. Priya Sharma',
      email: 'priya.sharma@college.edu',
      department: 'Mathematics',
      maxHoursPerWeek: 35,
      isActive: true
    }
  ],
  classrooms: [
    {
      _id: 'local-room-1',
      name: 'CS-101',
      capacity: 60,
      type: 'Lecture Hall',
      isActive: true
    },
    {
      _id: 'local-room-2',
      name: 'Lab-A',
      capacity: 30,
      type: 'Lab',
      isActive: true
    }
  ],
  subjects: [
    {
      _id: 'local-subject-1',
      code: 'CS101',
      name: 'Introduction to Programming',
      credits: 4,
      type: 'Theory',
      hoursPerWeek: 4,
      department: 'Computer Science',
      semester: 1,
      nepCompliant: true
    },
    {
      _id: 'local-subject-2',
      code: 'MATH101',
      name: 'Calculus',
      credits: 3,
      type: 'Theory',
      hoursPerWeek: 3,
      department: 'Mathematics',
      semester: 1,
      nepCompliant: false
    }
  ],
  timetables: [
    {
      _id: 'local-timetable-1',
      name: 'Computer Science Sem 1 Timetable',
      department: 'Computer Science',
      semester: 1,
      academicYear: '2024-25',
      entries: [],
      isActive: true
    }
  ]
};

function App() {
  const [dbStatus, setDbStatus] = useState('checking');
  const [demoMode, setDemoMode] = useState(true);
  const [backendOnline, setBackendOnline] = useState(false);

  useEffect(() => {
    checkBackendStatus();
  }, []);

  const checkBackendStatus = async () => {
    try {
      const response = await fetch('/api/health');
      if (response.ok) {
        const data = await response.json();
        setDbStatus(data.database.mode === 'MONGODB' ? 'connected' : 'demo');
        setDemoMode(data.database.mode === 'DEMO_MODE');
        setBackendOnline(true);
      } else {
        throw new Error('Backend not responding');
      }
    } catch (error) {
      console.log('Backend not available, using local demo data');
      setDbStatus('demo');
      setDemoMode(true);
      setBackendOnline(false);
    }
  };

  if (dbStatus === 'checking') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full mx-4 text-center">
          <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-calendar-check text-white text-2xl"></i>
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            NEP SmartScheduler
          </h1>
          <p className="text-gray-600 mb-6">
            Loading Admin Panel...
          </p>
          <div className="loading-spinner mx-auto mb-4"></div>
          <p className="text-sm text-gray-500">
            Checking backend connection...
          </p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="flex h-screen bg-gray-50">
        <Sidebar 
          dbStatus={dbStatus} 
          demoMode={demoMode} 
          backendOnline={backendOnline}
        />
        <div className="flex-1 flex flex-col overflow-hidden">
          <Header 
            dbStatus={dbStatus} 
            demoMode={demoMode} 
            backendOnline={backendOnline}
          />
          <DatabaseStatus 
            dbStatus={dbStatus} 
            demoMode={demoMode} 
            backendOnline={backendOnline}
            onRetry={checkBackendStatus}
          />
          <main className="flex-1 overflow-x-hidden overflow-y-auto p-6">
            <Routes>
              <Route path="/" element={
                <Dashboard 
                  demoMode={demoMode} 
                  backendOnline={backendOnline}
                  localData={localDemoData}
                />
              } />
              <Route path="/teachers" element={
                <TeacherManagement 
                  demoMode={demoMode} 
                  backendOnline={backendOnline}
                  localData={localDemoData}
                />
              } />
              <Route path="/classrooms" element={
                <ClassroomManagement 
                  demoMode={demoMode} 
                  backendOnline={backendOnline}
                  localData={localDemoData}
                />
              } />
              <Route path="/subjects" element={
                <SubjectManagement 
                  demoMode={demoMode} 
                  backendOnline={backendOnline}
                  localData={localDemoData}
                />
              } />
              <Route path="/generate" element={
                <TimetableGenerator 
                  demoMode={demoMode} 
                  backendOnline={backendOnline}
                />
              } />
              <Route path="/timetable" element={
                <TimetableView 
                  demoMode={demoMode} 
                  backendOnline={backendOnline}
                  localData={localDemoData}
                />
              } />
              <Route path="/ai-assistant" element={
                <AIAssistant 
                  demoMode={demoMode} 
                  backendOnline={backendOnline}
                />
              } />
            </Routes>
          </main>
        </div>
        <Toaster position="top-right" />
      </div>
    </Router>
  );
}

export default App;
