import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';



// Add this section to the Dashboard component

{/* Teacher Statistics */}
<div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
  <h3 className="text-lg font-semibold text-gray-900 mb-4">Teacher Overview</h3>
  <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-4">
    <div className="text-center p-4 border border-gray-200 rounded-lg">
      <i className="fas fa-chalkboard-teacher text-blue-500 text-2xl mb-2"></i>
      <p className="text-2xl font-bold text-gray-900">{stats.teachers}</p>
      <p className="text-sm text-gray-600">Total Teachers</p>
    </div>
    <div className="text-center p-4 border border-gray-200 rounded-lg">
      <i className="fas fa-clock text-green-500 text-2xl mb-2"></i>
      <p className="text-2xl font-bold text-gray-900">
        {teachers.reduce((total, teacher) => total + (teacher.maxHoursPerWeek || 0), 0)}
      </p>
      <p className="text-sm text-gray-600">Weekly Hours</p>
    </div>
    <div className="text-center p-4 border border-gray-200 rounded-lg">
      <i className="fas fa-book text-purple-500 text-2xl mb-2"></i>
      <p className="text-2xl font-bold text-gray-900">
        {teachers.reduce((total, teacher) => total + (teacher.subjects?.length || 0), 0)}
      </p>
      <p className="text-sm text-gray-600">Total Subjects</p>
    </div>
    <div className="text-center p-4 border border-gray-200 rounded-lg">
      <i className="fas fa-building text-orange-500 text-2xl mb-2"></i>
      <p className="text-2xl font-bold text-gray-900">
        {new Set(teachers.map(t => t.department)).size}
      </p>
      <p className="text-sm text-gray-600">Departments</p>
    </div>
  </div>
</div>

const Dashboard = ({ demoMode, backendOnline, localData }) => {
  const [stats, setStats] = useState({
    teachers: 0,
    classrooms: 0,
    subjects: 0,
    timetables: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, [demoMode, backendOnline, localData]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      if (!backendOnline) {
        // Use local demo data
        setStats({
          teachers: localData.teachers.length,
          classrooms: localData.classrooms.length,
          subjects: localData.subjects.length,
          timetables: localData.timetables.length
        });
        return;
      }

      // Try to fetch from backend
      const [teachersRes, classroomsRes, subjectsRes, timetablesRes] = await Promise.all([
        axios.get('/api/teachers'),
        axios.get('/api/classrooms'),
        axios.get('/api/subjects'),
        axios.get('/api/timetable')
      ]);

      setStats({
        teachers: teachersRes.data.count || 0,
        classrooms: classroomsRes.data.count || 0,
        subjects: subjectsRes.data.count || 0,
        timetables: timetablesRes.data.count || 0
      });
    } catch (error) {
      console.error('Dashboard error:', error);
      // Fallback to local data
      setStats({
        teachers: localData.teachers.length,
        classrooms: localData.classrooms.length,
        subjects: localData.subjects.length,
        timetables: localData.timetables.length
      });
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ title, value, icon, color }) => (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
      <div className="flex items-center">
        <div className={`p-3 rounded-lg ${color}`}>
          <i className={`${icon} text-white text-xl`}></i>
        </div>
        <div className="ml-4">
          <p className="text-sm font-medium text-gray-600">{title}</p>
          <p className="text-2xl font-bold text-gray-900">
            {loading ? '...' : value}
          </p>
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          {!backendOnline && (
            <p className="text-red-600 mt-1 flex items-center">
              <i className="fas fa-exclamation-triangle mr-1"></i>
              Backend not available - Using local demo data
            </p>
          )}
          {backendOnline && demoMode && (
            <p className="text-yellow-600 mt-1 flex items-center">
              <i className="fas fa-info-circle mr-1"></i>
              Using backend demo data
            </p>
          )}
        </div>
        <button
          onClick={fetchDashboardData}
          className="bg-primary-500 hover:bg-primary-600 text-white px-4 py-2 rounded-lg flex items-center space-x-2"
        >
          <i className="fas fa-refresh"></i>
          <span>Refresh</span>
        </button>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Teachers"
          value={stats.teachers}
          icon="fas fa-chalkboard-teacher"
          color="bg-blue-500"
        />
        <StatCard
          title="Classrooms"
          value={stats.classrooms}
          icon="fas fa-door-open"
          color="bg-green-500"
        />
        <StatCard
          title="Subjects"
          value={stats.subjects}
          icon="fas fa-book"
          color="bg-purple-500"
        />
        <StatCard
          title="Timetables"
          value={stats.timetables}
          icon="fas fa-calendar-alt"
          color="bg-orange-500"
        />
      </div>

      {/* Connection Status */}
      {!backendOnline && (
        <div className="bg-red-50 border border-red-200 rounded-lg p-6">
          <div className="flex items-center">
            <i className="fas fa-exclamation-triangle text-red-600 text-xl mr-3"></i>
            <div>
              <h3 className="text-lg font-semibold text-red-800">
                Backend Server Not Available
              </h3>
              <p className="text-red-700">
                The backend server is not running. Please start the backend server on port 8080 to enable full functionality.
              </p>
              <p className="text-sm text-red-600 mt-2">
                Current mode: Local Demo Data
              </p>
            </div>
          </div>
        </div>
      )}

      {/* Quick Actions */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-6">
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            Quick Actions
          </h3>
          <div className="space-y-3">
            <button 
              onClick={() => window.location.href = '/generate'}
              className="w-full bg-primary-50 hover:bg-primary-100 text-primary-700 px-4 py-3 rounded-lg flex items-center space-x-3 text-left"
            >
              <i className="fas fa-calendar-plus text-primary-500"></i>
              <span>Generate New Timetable</span>
            </button>
            <button 
              onClick={() => window.location.href = '/ai-assistant'}
              className="w-full bg-green-50 hover:bg-green-100 text-green-700 px-4 py-3 rounded-lg flex items-center space-x-3 text-left"
            >
              <i className="fas fa-robot text-green-500"></i>
              <span>AI Assistant</span>
            </button>
            <button className="w-full bg-purple-50 hover:bg-purple-100 text-purple-700 px-4 py-3 rounded-lg flex items-center space-x-3 text-left">
              <i className="fas fa-download text-purple-500"></i>
              <span>Export Data</span>
            </button>
          </div>
        </div>

        {/* System Status */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4">
            System Status
          </h3>
          <div className="space-y-4">
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">Frontend</span>
              <span className="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">
                Online
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">Backend API</span>
              <span className={`px-2 py-1 text-xs rounded-full ${
                backendOnline 
                  ? 'bg-green-100 text-green-800' 
                  : 'bg-red-100 text-red-800'
              }`}>
                {backendOnline ? 'Online' : 'Offline'}
              </span>
            </div>
            <div className="flex justify-between items-center">
              <span className="text-sm text-gray-600">Data Mode</span>
              <span className={`px-2 py-1 text-xs rounded-full ${
                demoMode 
                  ? 'bg-yellow-100 text-yellow-800' 
                  : 'bg-green-100 text-green-800'
              }`}>
                {demoMode ? 'Demo Mode' : 'MongoDB Mode'}
              </span>
            </div>
          </div>
        </div>

        {/* Setup Instructions */}
        <div className="bg-blue-50 border border-blue-200 rounded-lg p-6">
          <h3 className="text-lg font-semibold text-blue-900 mb-3">
            Setup Instructions
          </h3>
          <div className="space-y-2 text-sm text-blue-800">
            <p><strong>To enable backend:</strong></p>
            <ol className="list-decimal list-inside space-y-1 ml-4">
              <li>Open terminal in backend folder</li>
              <li>Run: <code>npm run dev</code></li>
              <li>Ensure it runs on port 8080</li>
              <li>Refresh this page</li>
            </ol>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Dashboard;
