import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const TeacherRoutines = ({ demoMode, backendOnline, localData, isAdmin }) => {
  const [teachers, setTeachers] = useState([]);
  const [selectedTeacher, setSelectedTeacher] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchTeachers();
  }, [demoMode, backendOnline, localData]);

  const fetchTeachers = async () => {
    try {
      setLoading(true);

      if (!backendOnline) {
        setTeachers(localData.teachers);
        return;
      }

      const response = await axios.get('/api/teachers');
      setTeachers(response.data.data || []);
    } catch (error) {
      console.error('Error fetching teachers:', error);
      setTeachers(localData.teachers);
    } finally {
      setLoading(false);
    }
  };

  const days = ['Monday', 'Tuesday', 'Wednesday', 'Thursday', 'Friday', 'Saturday'];
  const timeSlots = [
    '08:00-09:00', '09:00-10:00', '10:00-11:00', '11:00-12:00',
    '12:00-13:00', '14:00-15:00', '15:00-16:00', '16:00-17:00'
  ];

  const getTeacherSchedule = (teacher) => {
    if (teacher.routines) {
      return teacher.routines;
    }

    // Generate schedule from timetables if routines not available
    const schedule = [];
    days.forEach(day => {
      timeSlots.forEach(time => {
        schedule.push({
          day,
          time,
          subject: 'Available',
          classroom: '-',
          type: 'Free'
        });
      });
    });
    return schedule;
  };

  if (loading) {
    return (
      <div className="flex justify-center items-center h-64">
        <div className="loading-spinner"></div>
      </div>
    );
  }

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Teacher Routines</h1>
          <p className="text-gray-600 mt-1">View teacher schedules and routines</p>
        </div>
        {isAdmin && (
          <button className="bg-green-500 hover:bg-green-600 text-white px-4 py-2 rounded-lg flex items-center space-x-2">
            <i className="fas fa-plus"></i>
            <span>Add Routine</span>
          </button>
        )}
      </div>

      {/* Teacher Selection */}
      <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
        <h2 className="text-xl font-semibold text-gray-900 mb-4">Select Teacher</h2>
        <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-3 gap-4">
          {teachers.map(teacher => (
            <div
              key={teacher._id}
              onClick={() => setSelectedTeacher(teacher)}
              className={`p-4 border rounded-lg cursor-pointer transition-all ${
                selectedTeacher?._id === teacher._id
                  ? 'border-blue-500 bg-blue-50'
                  : 'border-gray-200 hover:border-blue-300'
              }`}
            >
              <div className="flex items-center space-x-3">
                <div className="w-12 h-12 bg-blue-500 rounded-full flex items-center justify-center">
                  <i className="fas fa-user text-white"></i>
                </div>
                <div>
                  <h3 className="font-semibold text-gray-900">{teacher.name}</h3>
                  <p className="text-sm text-gray-600">{teacher.department}</p>
                  <p className="text-xs text-gray-500">
                    {teacher.subjects?.length || 0} subjects
                  </p>
                </div>
              </div>
            </div>
          ))}
        </div>
      </div>

      {/* Teacher Routine */}
      {selectedTeacher && (
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 overflow-hidden">
          <div className="bg-gradient-to-r from-blue-500 to-purple-600 p-6 text-white">
            <div className="flex justify-between items-start">
              <div>
                <h2 className="text-2xl font-bold">{selectedTeacher.name}</h2>
                <p className="text-blue-100">{selectedTeacher.department}</p>
                <p className="text-blue-100 text-sm mt-1">
                  {selectedTeacher.qualifications?.join(', ')}
                </p>
              </div>
              <div className="text-right">
                <p className="text-blue-100">Max Hours: {selectedTeacher.maxHoursPerWeek}/week</p>
                <p className="text-blue-100">Email: {selectedTeacher.email}</p>
              </div>
            </div>
          </div>

          {/* Weekly Schedule */}
          <div className="p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Weekly Schedule</h3>
            <div className="overflow-x-auto">
              <table className="w-full">
                <thead>
                  <tr className="bg-gray-50">
                    <th className="px-4 py-3 text-left text-sm font-medium text-gray-700">Time/Day</th>
                    {days.map(day => (
                      <th key={day} className="px-4 py-3 text-center text-sm font-medium text-gray-700">
                        {day}
                      </th>
                    ))}
                  </tr>
                </thead>
                <tbody className="divide-y divide-gray-200">
                  {timeSlots.map(timeSlot => (
                    <tr key={timeSlot}>
                      <td className="px-4 py-3 text-sm font-medium text-gray-900 bg-gray-50">
                        {timeSlot}
                      </td>
                      {days.map(day => {
                        const routine = getTeacherSchedule(selectedTeacher).find(
                          r => r.day === day && r.time === timeSlot
                        );

                        return (
                          <td key={day} className="px-4 py-3 text-center">
                            {routine ? (
                              <div className={`p-2 rounded text-xs ${
                                routine.type === 'Lecture' ? 'bg-blue-100 text-blue-800' :
                                routine.type === 'Lab' ? 'bg-purple-100 text-purple-800' :
                                routine.type === 'Tutorial' ? 'bg-green-100 text-green-800' :
                                'bg-gray-100 text-gray-800'
                              }`}>
                                <div className="font-semibold">{routine.subject}</div>
                                <div>{routine.classroom}</div>
                                <div className="text-xs opacity-75">{routine.type}</div>
                              </div>
                            ) : (
                              <div className="p-2 bg-gray-50 rounded text-gray-500 text-xs">
                                Free
                              </div>
                            )}
                          </td>
                        );
                      })}
                    </tr>
                  ))}
                </tbody>
              </table>
            </div>
          </div>

          {/* Subject Details */}
          <div className="border-t border-gray-200 p-6">
            <h3 className="text-lg font-semibold text-gray-900 mb-4">Assigned Subjects</h3>
            <div className="grid grid-cols-1 md:grid-cols-2 gap-4">
              {selectedTeacher.subjects?.map((subject, index) => (
                <div key={index} className="border border-gray-200 rounded-lg p-4">
                  <div className="flex justify-between items-start">
                    <div>
                      <h4 className="font-semibold text-gray-900">
                        {typeof subject === 'object' ? subject.name : subject}
                      </h4>
                      <p className="text-sm text-gray-600">
                        {typeof subject === 'object' ? subject.code : 'Subject Code'}
                      </p>
                    </div>
                    {isAdmin && (
                      <button className="text-red-500 hover:text-red-700">
                        <i className="fas fa-times"></i>
                      </button>
                    )}
                  </div>
                </div>
              ))}
            </div>
          </div>
        </div>
      )}

      {!selectedTeacher && teachers.length > 0 && (
        <div className="bg-yellow-50 border border-yellow-200 rounded-lg p-6 text-center">
          <i className="fas fa-info-circle text-yellow-600 text-2xl mb-3"></i>
          <h3 className="text-lg font-semibold text-yellow-800">Select a Teacher</h3>
          <p className="text-yellow-700">Please select a teacher from the list above to view their routine</p>
        </div>
      )}
    </div>
  );
};

export default TeacherRoutines;
