import React, { useState, useEffect } from 'react';
import axios from 'axios';
import toast from 'react-hot-toast';

const Dashboard = ({ demoMode }) => {
  const [stats, setStats] = useState({
    teachers: 0,
    classrooms: 0,
    subjects: 0,
    timetables: 0
  });
  const [recentTimetables, setRecentTimetables] = useState([]);
  const [nepStats, setNepStats] = useState({
    skillSubjects: 0,
    vocationalSubjects: 0,
    interdisciplinarySubjects: 0
  });
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    fetchDashboardData();
  }, [demoMode]);

  const fetchDashboardData = async () => {
    try {
      setLoading(true);

      if (demoMode) {
        // Use enhanced demo data directly
        setStats({
          teachers: 6,
          classrooms: 8,
          subjects: 18,
          timetables: 2
        });
        setRecentTimetables([
          {
            name: 'Computer Science Semester 1',
            department: 'Computer Science',
            semester: 1,
            generatedDate: '2024-01-15'
          },
          {
            name: 'Electronics Semester 1', 
            department: 'Electronics',
            semester: 1,
            generatedDate: '2024-01-14'
          }
        ]);
        setNepStats({
          skillSubjects: 3,
          vocationalSubjects: 2,
          interdisciplinarySubjects: 1
        });
        return;
      }

      const [teachersRes, classroomsRes, subjectsRes, timetablesRes] = await Promise.all([
        axios.get('/api/teachers'),
        axios.get('/api/classrooms'),
        axios.get('/api/subjects'),
        axios.get('/api/timetable')
      ]);

      const subjects = subjectsRes.data.data || [];
      const nepCompliantSubjects = subjects.filter(subject => subject.nepCompliant);

      setStats({
        teachers: teachersRes.data.count || 0,
        classrooms: classroomsRes.data.count || 0,
        subjects: subjectsRes.data.count || 0,
        timetables: timetablesRes.data.count || 0
      });

      setNepStats({
        skillSubjects: subjects.filter(s => s.type === 'Skill').length,
        vocationalSubjects: subjects.filter(s => s.type === 'Vocational').length,
        interdisciplinarySubjects: subjects.filter(s => s.type === 'Interdisciplinary').length
      });

      setRecentTimetables((timetablesRes.data.data || []).slice(0, 3));
    } catch (error) {
      toast.error('Failed to load dashboard data');
      console.error('Dashboard error:', error);
      // Fallback to demo data
      setStats({
        teachers: 6,
        classrooms: 8,
        subjects: 18,
        timetables: 2
      });
      setNepStats({
        skillSubjects: 3,
        vocationalSubjects: 2,
        interdisciplinarySubjects: 1
      });
    } finally {
      setLoading(false);
    }
  };

  const StatCard = ({ title, value, icon, color, description }) => (
    <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6 hover:shadow-md transition-shadow">
      <div className="flex items-start justify-between">
        <div className="flex items-center">
          <div className={`p-3 rounded-lg ${color}`}>
            <i className={`${icon} text-white text-xl`}></i>
          </div>
          <div className="ml-4">
            <p className="text-sm font-medium text-gray-600">{title}</p>
            <p className="text-2xl font-bold text-gray-900">
              {loading ? '...' : value}
            </p>
            {description && (
              <p className="text-xs text-gray-500 mt-1">{description}</p>
            )}
          </div>
        </div>
      </div>
    </div>
  );

  return (
    <div className="space-y-6">
      <div className="flex justify-between items-center">
        <div>
          <h1 className="text-3xl font-bold text-gray-900">Dashboard</h1>
          {demoMode && (
            <p className="text-yellow-600 mt-1 flex items-center">
              <i className="fas fa-info-circle mr-1"></i>
              Showing comprehensive demo data with 6 teachers, 8 classrooms, and 18 subjects
            </p>
          )}
        </div>
        <button
          onClick={fetchDashboardData}
          className="bg-primary-500 hover:bg-primary-600 text-white px-4 py-2 rounded-lg flex items-center space-x-2"
        >
          <i className="fas fa-refresh"></i>
          <span>Refresh</span>
        </button>
      </div>

      {/* Stats Grid */}
      <div className="grid grid-cols-1 md:grid-cols-2 lg:grid-cols-4 gap-6">
        <StatCard
          title="Total Teachers"
          value={stats.teachers}
          icon="fas fa-chalkboard-teacher"
          color="bg-blue-500"
          description="Across all departments"
        />
        <StatCard
          title="Classrooms & Labs"
          value={stats.classrooms}
          icon="fas fa-door-open"
          color="bg-green-500"
          description="Lecture halls and laboratories"
        />
        <StatCard
          title="Subjects"
          value={stats.subjects}
          icon="fas fa-book"
          color="bg-purple-500"
          description="Including NEP-compliant courses"
        />
        <StatCard
          title="Timetables"
          value={stats.timetables}
          icon="fas fa-calendar-alt"
          color="bg-orange-500"
          description="Generated schedules"
        />
      </div>

      <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
        {/* NEP Compliance Overview */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
            <i className="fas fa-graduation-cap text-green-500 mr-2"></i>
            NEP 2020 Compliance
          </h3>
          <div className="space-y-4">
            <div className="flex justify-between items-center p-3 bg-green-50 rounded-lg">
              <div className="flex items-center space-x-3">
                <i className="fas fa-tools text-green-600"></i>
                <span className="text-sm font-medium text-green-800">Skill Subjects</span>
              </div>
              <span className="text-lg font-bold text-green-900">{nepStats.skillSubjects}</span>
            </div>
            <div className="flex justify-between items-center p-3 bg-blue-50 rounded-lg">
              <div className="flex items-center space-x-3">
                <i className="fas fa-briefcase text-blue-600"></i>
                <span className="text-sm font-medium text-blue-800">Vocational Courses</span>
              </div>
              <span className="text-lg font-bold text-blue-900">{nepStats.vocationalSubjects}</span>
            </div>
            <div className="flex justify-between items-center p-3 bg-purple-50 rounded-lg">
              <div className="flex items-center space-x-3">
                <i className="fas fa-exchange-alt text-purple-600"></i>
                <span className="text-sm font-medium text-purple-800">Interdisciplinary</span>
              </div>
              <span className="text-lg font-bold text-purple-900">{nepStats.interdisciplinarySubjects}</span>
            </div>
          </div>
        </div>

        {/* Recent Timetables */}
        <div className="bg-white rounded-lg shadow-sm border border-gray-200 p-6">
          <h3 className="text-lg font-semibold text-gray-900 mb-4 flex items-center">
            <i className="fas fa-clock text-primary-500 mr-2"></i>
            Recent Timetables
          </h3>
          <div className="space-y-3">
            {recentTimetables.map((timetable, index) => (
              <div key={index} className="flex items-center justify-between p-3 border border-gray-200 rounded-lg hover:bg-gray-50">
                <div>
                  <p className="text-sm font-medium text-gray-900">{timetable.name}</p>
                  <p className="text-xs text-gray-500">{timetable.department} â€¢ Sem {timetable.semester}</p>
                </div>
