                <p className="text-xs text-primary-600">Create new schedule with AI</p>
              </div>
            </button>
            <button 
              onClick={() => window.location.href = '/ai-assistant'}
              className="w-full bg-green-50 hover:bg-green-100 text-green-700 px-4 py-3 rounded-lg flex items-center space-x-3 text-left transition-colors"
            >
              <i className="fas fa-robot text-green-500"></i>
              <div>
                <span className="font-medium">AI Assistant</span>
                <p className="text-xs text-green-600">Get help with scheduling</p>
              </div>
            </button>
            <button 
              onClick={() => window.location.href = '/teachers'}
