import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import axios from 'axios';
import Dashboard from './components/Dashboard';
import TeacherManagement from './components/TeacherManagement';
import ClassroomManagement from './components/ClassroomManagement';
import SubjectManagement from './components/SubjectManagement';
import TimetableGenerator from './components/TimetableGenerator';
import TimetableView from './components/TimetableView';
import AIAssistant from './components/AIAssistant';
import DatabaseManager from './components/DatabaseManager';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import DatabaseStatus from './components/DatabaseStatus';

function App() {
  const [dbStatus, setDbStatus] = useState('checking');
  const [demoMode, setDemoMode] = useState(false);
  const [currentView, setCurrentView] = useState('dashboard');

  useEffect(() => {
    checkDatabaseStatus();
  }, []);

  const checkDatabaseStatus = async () => {
    try {
      const response = await axios.get('/api/health');
      setDbStatus(response.data.database.mode === 'MONGODB' ? 'connected' : 'demo');
      setDemoMode(response.data.database.mode === 'DEMO_MODE');
    } catch (error) {
      setDbStatus('error');
      setDemoMode(true);
    }
  };

  const handleModeChange = (newMode) => {
    setDemoMode(newMode === 'demo');
    setDbStatus(newMode === 'demo' ? 'demo' : 'connected');
    checkDatabaseStatus();
  };

  if (dbStatus === 'checking') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full mx-4 text-center">
          <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-calendar-check text-white text-2xl"></i>
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            NEP SmartScheduler
          </h1>
          <p className="text-gray-600 mb-6">
            Loading Admin Panel...
          </p>
          <div className="loading-spinner mx-auto mb-4"></div>
          <p className="text-sm text-gray-500">
            Checking database connection...
          </p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="flex h-screen bg-gray-50">
        <Sidebar 
          dbStatus={dbStatus} 
          demoMode={demoMode} 
          currentView={currentView}
          onViewChange={setCurrentView}
        />
        <div className="flex-1 flex flex-col overflow-hidden">
          <Header dbStatus={dbStatus} demoMode={demoMode} />
          <DatabaseStatus 
            dbStatus={dbStatus} 
            demoMode={demoMode} 
            onRetry={checkDatabaseStatus}
          />
          <main className="flex-1 overflow-x-hidden overflow-y-auto p-6">
            <Routes>
              <Route path="/" element={<Dashboard demoMode={demoMode} />} />
              <Route path="/teachers" element={<TeacherManagement demoMode={demoMode} />} />
              <Route path="/classrooms" element={<ClassroomManagement demoMode={demoMode} />} />
              <Route path="/subjects" element={<SubjectManagement demoMode={demoMode} />} />
              <Route path="/generate" element={<TimetableGenerator demoMode={demoMode} />} />
              <Route path="/timetable" element={<TimetableView demoMode={demoMode} />} />
              <Route path="/ai-assistant" element={<AIAssistant demoMode={demoMode} />} />
              <Route path="/database" element={
                <DatabaseManager 
                  currentMode={demoMode ? 'demo' : 'mongodb'} 
                  onModeChange={handleModeChange}
                />
              } />
            </Routes>
          </main>
        </div>
        <Toaster position="top-right" />
      </div>
    </Router>
  );
}

export default App;
