import React, { useState, useEffect } from 'react';
import { BrowserRouter as Router, Routes, Route } from 'react-router-dom';
import { Toaster } from 'react-hot-toast';
import axios from 'axios';
import Dashboard from './components/Dashboard';
import TeacherManagement from './components/TeacherManagement';
import ClassroomManagement from './components/ClassroomManagement';
import SubjectManagement from './components/SubjectManagement';
import TimetableGenerator from './components/TimetableGenerator';
import TimetableView from './components/TimetableView';
import AIAssistant from './components/AIAssistant';
import DatabaseManager from './components/DatabaseManager';
import TeacherRoutines from './components/TeacherRoutines';
import Sidebar from './components/Sidebar';
import Header from './components/Header';
import DatabaseStatus from './components/DatabaseStatus';
import Footer from './components/Footer';
import AdminLogin from './components/AdminLogin';

// Demo data for when backend is not available
const localDemoData = {
  teachers: [
    {
      _id: 'local-teacher-1',
      name: 'Dr. Rajesh Kumar',
      email: 'rajesh.kumar@college.edu',
      department: 'Computer Science',
      maxHoursPerWeek: 40,
      qualifications: ['PhD in Computer Science', 'M.Tech in AI'],
      subjects: ['CS101', 'CS102'],
      routines: [
        { day: 'Monday', time: '09:00-10:00', subject: 'CS101', classroom: 'CS-101', type: 'Lecture' },
        { day: 'Monday', time: '11:00-12:00', subject: 'CS102', classroom: 'CS-102', type: 'Lecture' },
        { day: 'Tuesday', time: '09:00-11:00', subject: 'CS101 Lab', classroom: 'Lab-A', type: 'Lab' }
      ],
      isActive: true
    },
    {
      _id: 'local-teacher-2',
      name: 'Prof. Priya Sharma',
      email: 'priya.sharma@college.edu',
      department: 'Mathematics', 
      maxHoursPerWeek: 35,
      qualifications: ['PhD in Mathematics', 'M.Sc in Statistics'],
      subjects: ['MATH101', 'MATH102'],
      routines: [
        { day: 'Monday', time: '10:00-11:00', subject: 'MATH101', classroom: 'CS-101', type: 'Lecture' },
        { day: 'Wednesday', time: '14:00-16:00', subject: 'MATH102', classroom: 'CS-102', type: 'Tutorial' }
      ],
      isActive: true
    }
  ],
  classrooms: [
    {
      _id: 'local-room-1',
      name: 'CS-101',
      capacity: 60,
      type: 'Lecture Hall',
      facilities: ['Projector', 'Whiteboard', 'AC', 'Sound System'],
      schedule: [
        { day: 'Monday', time: '09:00-10:00', subject: 'CS101', teacher: 'Dr. Rajesh Kumar' },
        { day: 'Monday', time: '10:00-11:00', subject: 'MATH101', teacher: 'Prof. Priya Sharma' }
      ],
      isActive: true
    }
  ],
  subjects: [
    {
      _id: 'local-subject-1',
      code: 'CS101',
      name: 'Introduction to Programming',
      credits: 4,
      type: 'Theory',
      hoursPerWeek: 4,
      department: 'Computer Science',
      semester: 1,
      nepCompliant: true,
      teachers: ['Dr. Rajesh Kumar'],
      classes: ['CS Sem 1', 'CS Sem 2']
    }
  ],
  timetables: [
    {
      _id: 'local-timetable-1',
      name: 'Computer Science Semester 1 Timetable',
      department: 'Computer Science',
      semester: 1,
      academicYear: '2024-25',
      entries: [
        {
          day: 'Monday',
          startTime: '09:00',
          endTime: '10:00',
          subject: { name: 'Introduction to Programming', code: 'CS101' },
          teacher: { name: 'Dr. Rajesh Kumar' },
          classroom: { name: 'CS-101' },
          class: 'CS Sem 1',
          type: 'Lecture'
        }
      ],
      isActive: true
    }
  ]
};

function App() {
  const [dbStatus, setDbStatus] = useState('checking');
  const [demoMode, setDemoMode] = useState(true);
  const [backendOnline, setBackendOnline] = useState(false);
  const [isAdmin, setIsAdmin] = useState(false);
  const [showAdminLogin, setShowAdminLogin] = useState(false);

  useEffect(() => {
    checkBackendStatus();
    // Check if admin is already logged in
    const adminToken = localStorage.getItem('adminToken');
    if (adminToken) {
      setIsAdmin(true);
    }
  }, []);

  const checkBackendStatus = async () => {
    try {
      const response = await fetch('/api/health');
      if (response.ok) {
        const data = await response.json();
        setDbStatus(data.database.mode === 'MONGODB' ? 'connected' : 'demo');
        setDemoMode(data.database.mode === 'DEMO_MODE');
        setBackendOnline(true);
      } else {
        throw new Error('Backend not responding');
      }
    } catch (error) {
      console.log('Backend not available, using local demo data');
      setDbStatus('demo');
      setDemoMode(true);
      setBackendOnline(false);
    }
  };

  const handleAdminLogin = (token, user) => {
    localStorage.setItem('adminToken', token);
    localStorage.setItem('adminUser', JSON.stringify(user));
    setIsAdmin(true);
    setShowAdminLogin(false);
  };

  const handleAdminLogout = () => {
    localStorage.removeItem('adminToken');
    localStorage.removeItem('adminUser');
    setIsAdmin(false);
  };

  if (dbStatus === 'checking') {
    return (
      <div className="min-h-screen bg-gradient-to-br from-blue-50 to-indigo-100 flex items-center justify-center">
        <div className="bg-white rounded-2xl shadow-xl p-8 max-w-md w-full mx-4 text-center">
          <div className="w-16 h-16 bg-blue-500 rounded-full flex items-center justify-center mx-auto mb-4">
            <i className="fas fa-calendar-check text-white text-2xl"></i>
          </div>
          <h1 className="text-3xl font-bold text-gray-900 mb-2">
            NEP SmartScheduler
          </h1>
          <p className="text-gray-600 mb-6">
            Loading Admin Panel...
          </p>
          <div className="loading-spinner mx-auto mb-4"></div>
          <p className="text-sm text-gray-500">
            Checking backend connection...
          </p>
        </div>
      </div>
    );
  }

  return (
    <Router>
      <div className="flex flex-col h-screen bg-gray-50">
        {/* Admin Login Modal */}
        {showAdminLogin && (
          <AdminLogin 
            onLogin={handleAdminLogin}
            onClose={() => setShowAdminLogin(false)}
          />
        )}

        <div className="flex flex-1 overflow-hidden">
          <Sidebar 
            dbStatus={dbStatus} 
            demoMode={demoMode} 
            backendOnline={backendOnline}
            isAdmin={isAdmin}
          />
          <div className="flex-1 flex flex-col overflow-hidden">
            <Header 
              dbStatus={dbStatus} 
              demoMode={demoMode} 
              backendOnline={backendOnline}
              isAdmin={isAdmin}
              onAdminLogout={handleAdminLogout}
            />
            <DatabaseStatus 
              dbStatus={dbStatus} 
              demoMode={demoMode} 
              backendOnline={backendOnline}
              onRetry={checkBackendStatus}
            />
            <main className="flex-1 overflow-x-hidden overflow-y-auto p-6">
              <Routes>
                <Route path="/" element={
                  <Dashboard 
                    demoMode={demoMode} 
                    backendOnline={backendOnline}
                    localData={localDemoData}
                    isAdmin={isAdmin}
                  />
                } />
                <Route path="/teachers" element={
                  <TeacherManagement 
                    demoMode={demoMode} 
                    backendOnline={backendOnline}
                    localData={localDemoData}
                    isAdmin={isAdmin}
                  />
                } />
                <Route path="/classrooms" element={
                  <ClassroomManagement 
                    demoMode={demoMode} 
                    backendOnline={backendOnline}
                    localData={localDemoData}
                    isAdmin={isAdmin}
                  />
                } />
                <Route path="/subjects" element={
                  <SubjectManagement 
                    demoMode={demoMode} 
                    backendOnline={backendOnline}
                    localData={localDemoData}
                    isAdmin={isAdmin}
                  />
                } />
                <Route path="/generate" element={
                  <TimetableGenerator 
                    demoMode={demoMode} 
                    backendOnline={backendOnline}
                    isAdmin={isAdmin}
                  />
                } />
                <Route path="/timetable" element={
                  <TimetableView 
                    demoMode={demoMode} 
                    backendOnline={backendOnline}
                    localData={localDemoData}
                    isAdmin={isAdmin}
                  />
                } />
                <Route path="/teacher-routines" element={
                  <TeacherRoutines 
                    demoMode={demoMode} 
                    backendOnline={backendOnline}
                    localData={localDemoData}
                    isAdmin={isAdmin}
                  />
                } />
                <Route path="/ai-assistant" element={
                  <AIAssistant 
                    demoMode={demoMode} 
                    backendOnline={backendOnline}
                  />
                } />
                <Route path="/database" element={
                  <DatabaseManager 
                    currentMode={demoMode ? 'demo' : 'mongodb'} 
                    onModeChange={checkBackendStatus}
                  />
                } />
              </Routes>
            </main>
          </div>
        </div>

        <Footer onAdminLogin={() => setShowAdminLogin(true)} />
        <Toaster position="top-right" />
      </div>
    </Router>
  );
}

export default App;
